"use client"

import type * as React from "react"
import { createFormHook, createFormHookContexts } from "@tanstack/react-form"
import type { z } from "zod"

import { Button } from "@/components/ui/button"
import { Checkbox } from "@/components/ui/checkbox"
import {
  Field,
  FieldContent,
  FieldDescription as FieldDescriptionComponent,
  FieldError,
  FieldLabel as FieldLabelComponent,
} from "@/components/ui/field"
import { Input } from "@/components/ui/input"
import { Select } from "@/components/ui/select"
import { Switch } from "@/components/ui/switch"
import { Textarea } from "@/components/ui/textarea"

export const { fieldContext, useFieldContext, formContext, useFormContext } =
  createFormHookContexts()

export function SubmitButton(props: React.ComponentProps<typeof Button>) {
  const form = useFormContext()

  return (
    <form.Subscribe selector={(state) => state.isSubmitting}>
      {(isSubmitting) => (
        <Button type="submit" disabled={isSubmitting} {...props} />
      )}
    </form.Subscribe>
  )
}

function FormInput(props: React.ComponentProps<typeof Input>) {
  const field = useFieldContext<string>()

  return (
    <Input
      id={field.name}
      name={field.name}
      value={field.state.value}
      placeholder={props.placeholder}
      onBlur={field.handleBlur}
      onChange={(e) => field.handleChange(e.target.value)}
      {...props}
    />
  )
}

function FormSelect(props: React.ComponentProps<typeof Select>) {
  const field = useFieldContext<string>()
  return (
    <Select
      name={field.name}
      value={field.state.value}
      onValueChange={(value) => field.handleChange(value)}
      {...props}
    />
  )
}

function FormTextarea(props: React.ComponentProps<typeof Textarea>) {
  const field = useFieldContext<string>()
  return (
    <Textarea
      id={field.name}
      name={field.name}
      value={field.state.value}
      placeholder={props.placeholder}
      onBlur={field.handleBlur}
      onChange={(e) => field.handleChange(e.target.value)}
      {...props}
    />
  )
}

function FormCheckbox(props: React.ComponentProps<typeof Checkbox>) {
  const field = useFieldContext<boolean>()
  return (
    <Checkbox
      id={field.name}
      name={field.name}
      checked={Boolean(field.state.value)}
      onCheckedChange={(checked) => field.handleChange(Boolean(checked))}
      {...props}
    />
  )
}

function FormSwitch(props: React.ComponentProps<typeof Switch>) {
  const field = useFieldContext<boolean>()
  return (
    <Switch
      id={field.name}
      name={field.name}
      checked={Boolean(field.state.value)}
      onCheckedChange={(checked) => field.handleChange(Boolean(checked))}
      {...props}
    />
  )
}

function FieldField(props: React.ComponentProps<typeof Field>) {
  const field = useFieldContext<string>()
  const isInvalid = field.state.meta.isTouched && !field.state.meta.isValid
  return <Field {...props} data-invalid={isInvalid} />
}

export const { useAppForm } = createFormHook({
  fieldComponents: {
    Input: FormInput,
    Select: FormSelect,
    Textarea: FormTextarea,
    Checkbox: FormCheckbox,
    Switch: FormSwitch,
    Label: FieldLabel,
    Description: FieldDescription,
    Message: FieldMessage,
    Field: FieldField,
    Content: FieldContent,
  },
  formComponents: {
    SubmitButton: SubmitButton,
  },
  fieldContext,
  formContext,
})

/**
 * Hook to create a TanStack form with Zod validation
 * Returns a type-safe FormApi with inferred form data type
 *
 * @example
 * const form = useForm({
 *   schema: z.object({ email: z.string().email() }),
 *   defaultValues: { email: '' },
 *   onSubmit: async (values) => console.log(values),
 * })
 * // form is typed as FormApi<{ email: string }>
 */
export function useForm<TSchema extends z.ZodType>({
  schema,
  defaultValues,
  onSubmit,
  validationMode = "onBlur",
}: {
  schema: TSchema
  defaultValues: z.infer<TSchema>
  onSubmit: (values: z.infer<TSchema>) => void | Promise<void>
  validationMode?: "onChange" | "onBlur" | "onSubmit"
}) {
  return useAppForm({
    defaultValues,
    validators: {
      [validationMode]: schema,
    },
    onSubmit: async ({ value }) => {
      await onSubmit(value as z.infer<TSchema>)
    },
  })
}

/**
 * Form wrapper component that provides form context and handles submission
 *
 * @example
 * const form = useForm({
 *   schema: z.object({ email: z.string().email() }),
 *   defaultValues: { email: '' },
 *   onSubmit: async (values) => console.log(values),
 * })
 *
 * <form onSubmit={(e) => { e.preventDefault(); form.handleSubmit() }}>
 *   <form.AppField name="email">
 *     {(field) => (
 *       <Field>
 *         <FormLabel>Email</FormLabel>
 *         <FieldContent>
 *           <field.Input type="email" placeholder="you@example.com" />
 *           <FormMessage />
 *         </FieldContent>
 *       </Field>
 *     )}
 *   </form.AppField>
 *   <form.AppForm>
 *     <form.SubmitButton>Submit</form.SubmitButton>
 *   </form.AppForm>
 * </form>
 */
export function Form({
  children,
  form,
  ...props
}: {
  children: React.ReactNode
  form: ReturnType<typeof useForm<any>>
} & Omit<React.ComponentProps<"form">, "onSubmit">) {
  return (
    <form.AppForm>
      <form
        onSubmit={(e) => {
          e.preventDefault()
          e.stopPropagation()
          void form.handleSubmit()
        }}
        {...props}
      >
        {children}
      </form>
    </form.AppForm>
  )
}

function FieldLabel(props: React.ComponentProps<typeof FieldLabelComponent>) {
  const field = useFieldContext<string>()

  return <FieldLabelComponent htmlFor={field.name} {...props} />
}

/**
 * Description text for form field - uses FieldDescription with TanStack Form integration
 */
function FieldDescription({
  className,
  ...props
}: React.ComponentProps<typeof FieldDescriptionComponent>) {
  const field = useFieldContext<string>()

  return (
    <FieldDescriptionComponent
      id={`${field.name}-form-item-description`}
      className={className}
      {...props}
    />
  )
}

function FieldMessage(props: React.ComponentProps<typeof FieldError>) {
  const field = useFieldContext<string>()
  const isInvalid = field.state.meta.isTouched && !field.state.meta.isValid

  return (
    <>
      {isInvalid ? (
        <FieldError {...props} errors={field.state.meta.errors} />
      ) : null}
    </>
  )
}

import type {
  ComponentPropsWithoutRef,
  ElementType,
  PropsWithChildren,
} from "react"
import type React from "react"
import type { VariantProps } from "class-variance-authority"
import { cva } from "class-variance-authority"

import { cn } from "@/lib/utils"

type PolymorphicAsProp<E extends ElementType> = {
  as?:
    | E
    | React.ComponentType<Omit<ComponentPropsWithoutRef<E>, "as">>
    | React.FunctionComponent<Omit<ComponentPropsWithoutRef<E>, "as">>
}

type PolymorphicProps<E extends ElementType> = PropsWithChildren<
  Omit<ComponentPropsWithoutRef<E>, "as"> & PolymorphicAsProp<E>
>

const typographyVariants = cva("", {
  variants: {
    variant: {
      h1: "scroll-m-20 font-caption text-4xl font-extrabold tracking-tight lg:text-5xl",
      h2: "scroll-m-20 text-xl font-semibold tracking-tight transition-colors md:text-2xl lg:text-3xl",
      h3: "scroll-m-20 text-xl font-semibold tracking-tight",
      p: "text-lg leading-7 not-first:mt-6",
      base: "text-lg",
      quote: "mt-6 border-l-2 pl-6 italic",
      code: "relative rounded bg-muted px-[0.3rem] py-[0.2rem] font-mono text-sm font-semibold",
      lead: "text-xl text-muted-foreground",
      large: "font-semibold md:text-lg",
      small: "text-sm font-medium leading-none",
      muted: "text-sm text-muted-foreground",
      link: "font-medium text-cyan-600 hover:underline dark:text-primary",
    },
  },
  defaultVariants: {
    variant: "p",
  },
})
type TypographyCvaProps = VariantProps<typeof typographyVariants>

const defaultElement = "base"

const defaultElementMapping: Record<
  NonNullable<TypographyCvaProps["variant"]>,
  ElementType
> = {
  h1: "h1",
  h2: "h2",
  h3: "h3",
  p: "p",
  quote: "blockquote" as "p",
  code: "code",
  lead: "p",
  large: "p",
  small: "p",
  muted: "p",
  link: "a",
  base: "p",
} as const

export function Typography<E extends ElementType = typeof defaultElement>({
  as,
  children,
  className,
  variant,
  ...restProps
}: PolymorphicProps<E> & TypographyCvaProps) {
  const Component: ElementType =
    as ?? defaultElementMapping[variant ?? "base"] ?? defaultElement

  return (
    <Component
      {...(restProps as ComponentPropsWithoutRef<E>)}
      className={cn(typographyVariants({ variant }), className)}
      data-typography
      data-typography-variant={variant}
    >
      {children}
    </Component>
  )
}
